package manojkumar.m.hotel;

public class leftupperfloyd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char num='a';
		for(int row=1;row<=5;row++)
		{
			for(int col=1;col<=row;col++,num++)
			{
				System.out.print(num);
			}
			System.out.println();
		}

		}

	}

